mapper.update:
update freight
deL_flag = '1', modifier = #{modifier}, modified_time = now()
where id = #{id}

mapper.select:
select id, name, site_id, status, type, remark, creator, created_time
from freight
where del_flag = '0'

    and id = #{id}


    and name LIKE CONCAT('%',#{name},'%')


    and site_id = #{siteId}

select id, name, site_id, status, type, remark, creator, created_time
from freight
where del_flag = '0'

    and name LIKE CONCAT('%',#{name},'%')


    and site_id = #{siteId}


    and status = #{status}


    and type = #{type}
