mapper.update:
update user
set del_flag = '1'
where id = #{id}

mapper.select:
select id, user_name,role_code,site_id, nick_name, password, sex, email, phone, avatar, status, unit, address, creator, created_time, modifier, modified_time from user where del_flag = '0' and role_code != 'ADMIN'
and user_name = #{userName}
limit 1

select id, user_name,role_code,site_id, nick_name, password, sex, email, phone, avatar, status, unit, address,
       creator,
       created_time,
       modifier,
       modified_time
from user
where del_flag = '0'
  and role_code != 'ADMIN'

    and user_name = #{userName}


    and nick_name LIKE CONCAT('%',#{nickName},'%')


    and unit LIKE CONCAT('%',#{unit},'%')


    and phone = #{phone}


    and role_code = #{roleCode}


    and site_id = #{roleCode}


    and status = #{status}
