mapper.update:
update warehouse
deL_flag = '1', modifier = #{modifier}, modified_time = now()
where id = #{id}

mapper.select:
select id, name, site_id, capacity, current_capacity, charge_person, phone, address, remark, status, creator, created_time from warehouse where del_flag = '0'
and id = #{id}
and name LIKE CONCAT('%',#{name},'%')

select id, name, site_id, capacity, current_capacity, charge_person, phone, address, remark, status, creator, created_time
from warehouse
where del_flag = '0'

    and name LIKE CONCAT('%',#{name},'%')


    and site_id = #{siteId}


    and phone = #{phone}


    and status = #{status}


    and address LIKE CONCAT('%',#{address},'%')


    and charge_person LIKE CONCAT('%',#{chargePerson},'%')
