mapper.select:
select id, user_name, nick_name, password, sex, email, phone, avatar, role_code, site_id, creator, created_time, modifier, modified_time from user where del_flag = '0'
and user_name = #{userName}
limit 1

select id, user_name, nick_name, password, sex, email, phone, avatar, role_code, site_id, status, creator, created_time, modifier, modified_time from user where del_flag = '0'
and user_name = #{userName}
and phone = #{phone}
and role_code = #{roleCode}
and site_id = #{roleCode}
and status = #{status}
